<script>
    // Tambahkan event listener untuk event input (saat pengguna mengetik)
    var searchInput = document.getElementById('searchInput');
    var searchResults = document.getElementById('searchResults');

    searchInput.addEventListener('input', function() {
        // Tangkap nilai input
        var searchTerm = searchInput.value.toLowerCase();

        // Jika input pencarian kosong
        if (searchTerm.trim() === '') {
            // Kosongkan hasil pencarian
            searchResults.innerHTML = '';
        } else {
            // Jika input pencarian tidak kosong
            // Bersihkan hasil pencarian sebelumnya
            searchResults.innerHTML = '';

            // Tangkap semua card film
            var cards = document.querySelectorAll('.card');

            // Iterasi melalui setiap card
            cards.forEach(function(card) {
                // Tangkap judul film dalam card
                var title = card.querySelector('.text').textContent.toLowerCase();
                
                // Bandingkan judul film dengan input pencarian
                if (title.includes(searchTerm)) {
                    // Jika cocok, tambahkan card ke hasil pencarian
                    searchResults.appendChild(card.cloneNode(true));
                }
            });
        }
    });
</script>

<script>
    function toggleDropdown() {
        var dropdown = document.getElementById("genreDropdown");
        dropdown.style.display = dropdown.style.display === "block" ? "none" : "block";
    }

    document.addEventListener('DOMContentLoaded', function() {
        var usernameElement = document.querySelector('.username');
        if (usernameElement) {
            usernameElement.style.color = '#fff'; // Ganti dengan warna yang diinginkan
        }
    });
</script>

</body>
</html>